    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        usuarioModel usuario = new usuarioModel();
        
        usuario.setCpf(cpf.getText());
        usuario.setNome(nome.getText());
        usuario.setSobrenome(sobrenome.getText());
        usuario.setNascimento(data_nasc.getText());
        usuario.setEmail(email.getText());
        usuario.setPais(pais.getText());
        usuario.setCidade(cidade.getText());
        usuario.setUf(uf.getText());
        usuario.setSenha(senha.getText());
        usuario.setNro_rg(rg.getText());
        usuario.setEmissor_rg(emissor.getText());
        usuario.setEstado_rg(estado.getText());
        usuario.setFone(telefone.getText());
        
        usuarioDAO usuarioD = new usuarioDAO();
        usuarioD.insert(usuario);
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        usuarioDAO usuarioD = new usuarioDAO();
        
        DBCursor cursor = usuarioD.findAll();
        
        DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
        
        while (model.getRowCount() > 0) {
            model.removeRow(0);
        }
        
        while (cursor.hasNext()) {
            BasicDBObject pessoa = (BasicDBObject) cursor.next();            
            model.addRow(new Object [] {pessoa.getString("cpf"), pessoa.getString("nome"), pessoa.getString("sobrenome"), pessoa.getString("fone")});
        }
    }//GEN-LAST:event_jButton2ActionPerformed

